mapper.update:
        update address SET
        update_time=now()
			,address=#{address}
			,province=#{province}
			,city=#{city}
			,post_code=#{postCode}
			,name=#{name}
			,phone=#{phone}
			,update_id=#{updateId}
			,def=#{def}
        where id = #{id} and buyer_id=#{buyerId}

mapper.select:
        SELECT
        *
        FROM address
        WHERE useflag=1
				AND buyer_id=#{buyerId}
				AND address=#{address}
				AND post_code=#{postCode}
				AND name=#{name}
				AND phone=#{phone}
				AND def=#{def}
        order by def desc
