mapper.update:
        update buyer SET
        update_time=now()
			,nick_name=#{nickName}
			,open_id=#{openId}
			,sex=#{sex}
			,avatar_url=#{avatarUrl}
			,update_id=#{updateId},
        where id = #{id}

update buyer SET
update_time=now(),
points=points-${totalFee}
WHERE id = #{id}

update buyer SET
update_time=now(),
points=points+${totalFee}
WHERE id = #{id}

update buyer
SET useflag=0 WHERE id IN

#{id}

mapper.select:
        SELECT
        *
        FROM buyer
        WHERE useflag=1
				AND nick_name=#{nickName}
				AND open_id=#{openId}
				AND sex=#{sex}
				AND avatar_url=#{avatarUrl}
				AND insert_time=#{insertTime}
				AND insert_id=#{insertId}
				AND update_time=#{updateTime}
				AND update_id=#{updateId}
